mapper.select:
    select 

    from t_leasing_app
    where (1=1 

        and sqdzt in 

					#{item}

    ) and (sqfqr=#{sqfqr})

        and id like  CONCAT(CONCAT('%',#{id}),'%')


        and name like  CONCAT(CONCAT('%',#{name}),'%')


        and date_format(sqfqsj,'%Y-%m-%d')=#{sqfqrq}

    order by sqfqsj desc

select app.* from t_leasing_app app left join t_leasing_check ck on ck.id=app.id where ((1=1
and app.sqdzt in
#{item}
) or (app.sqdzt='审批退回' and ck.shr=#{shr}) or (app.sqdzt='审核中' and ck.shr=#{shr}))
and app.id like CONCAT(CONCAT('%',#{id}),'%')
and app.name like CONCAT(CONCAT('%',#{name}),'%')
and date_format(app.sqfqsj,'%Y-%m-%d')=#{sqfqrq}
order by app.sqfqsj desc

select a.id, a.name, a.sqfqr, a.rzje, a.rzqx, b.rate, a.rzsxf, a.reserver1 as gpsfee, a.reserver1-a.reserver2 as gpsjw, a.reserver2 as jxsfy, a.reserver3 as pgf, a.fwf, b.yhk from t_leasing_app a left join t_product b on a.producttype=b.productname and a.productname=b.productseq and a.rzqx=b.periods
where a.id in
#{appid}
